/** @jsx jsx */

import { useBrand } from '@bank/core';
import { jsx } from '@emotion/core';

export function Component1({ look = 'look1', children }) {
	const { COLORS } = useBrand();

	const styleMap = {
		look1: COLORS.color1,
		look2: COLORS.color2,
		look3: COLORS.color3,
	};

	return (
		<div
			css={{
				label: `alert-${look}`,
				background: styleMap[look],
			}}
		>
			{children}
		</div>
	);
}
